import React, { useState } from 'react'
import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import Card from './Card'
import ExpensesFilter from './filter/ExpensesFilter'

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState('2019')

  const filterChangeHandler = (selectedYeat) => {
    console.log(selectedYeat)
    console.log('expenses.js')
    setFilteredYear(selectedYeat)
  }

  filteredExpenses = props.expenses.filter((expense) => {
    return expense.data.prototype.getYear == 2020
  })

  return (
    <Card className="expenses">
      <ExpensesFilter
        selectedf={filteredYear}
        onChangef={filterChangeHandler}
      />
      <div className="expenses">
        {props.expenses.map((expense) => (
          //       expense.data.prototype.getYear == 2020

          <ExpenseItem
            key={expense.id}
            title={expense.title}
            date={expense.date}
            amount={expense.amount}
          />
        ))}
      </div>
    </Card>
  )
}

export default Expenses
